package loops;

public class Multiplicationtable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
	for (int i=1;i<=5;i++) {
		System.out.println("Multiplication table for " +i);
		for (int j=1;j<=12;j++) {
			System.out.println(i +"*" + j +"="  + (i*j));
		}
	}
		
	}

}
